#ifndef ASPOSE_CELLS_DRAWING_BEVELPRESETTYPE_H
#define ASPOSE_CELLS_DRAWING_BEVELPRESETTYPE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Represents a preset for a type of bevel which can be applied to a shape in 3D.
/// </summary>
enum class BevelPresetType {
    /// <summary>
    /// No bevel
    /// </summary>
    None,
    /// <summary>
    /// Angle
    /// </summary>
    Angle,
    /// <summary>
    /// Art deco
    /// </summary>
    ArtDeco,
    /// <summary>
    /// Circle
    /// </summary>
    Circle,
    /// <summary>
    /// Convex
    /// </summary>
    Convex,
    /// <summary>
    /// Cool slant
    /// </summary>
    CoolSlant,
    /// <summary>
    /// Cross
    /// </summary>
    Cross,
    /// <summary>
    /// Divot
    /// </summary>
    Divot,
    /// <summary>
    /// Hard edge
    /// </summary>
    HardEdge,
    /// <summary>
    /// Relaxed inset
    /// </summary>
    RelaxedInset,
    /// <summary>
    /// Riblet
    /// </summary>
    Riblet,
    /// <summary>
    /// Slope
    /// </summary>
    Slope,
    /// <summary>
    /// Soft round
    /// </summary>
    SoftRound,
};

} } }

#endif
