#ifndef ASPOSE_CELLS_CHARTS_BUBBLESIZEREPRESENTS_H
#define ASPOSE_CELLS_CHARTS_BUBBLESIZEREPRESENTS_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents what the bubble size represents on a bubble chart.
/// </summary>
enum class BubbleSizeRepresents {
    /// <summary>
    /// Represents the value of <see cref="Series.BubbleSizes"/> is area of the bubble.
    /// </summary>
    SizeIsArea,
    /// <summary>
    /// Represents the value of <see cref="Series.BubbleSizes"/> is width of the bubble.
    /// </summary>
    SizeIsWidth,
};

} } }

#endif
