#ifndef ASPOSE_CELLS_CELLBORDERTYPE_H
#define ASPOSE_CELLS_CELLBORDERTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Enumerates a cell's border type.
/// </summary>
enum class CellBorderType {
    /// <summary>
    /// Represents thin dash-dotted line.
    /// </summary>
    DashDot = 9,
    /// <summary>
    /// Represents thin dash-dot-dotted line.
    /// </summary>
    DashDotDot = 11,
    /// <summary>
    /// Represents dashed line.
    /// </summary>
    Dashed = 3,
    /// <summary>
    /// Represents dotted line.
    /// </summary>
    Dotted = 4,
    /// <summary>
    /// Represents double line.
    /// </summary>
    Double = 6,
    /// <summary>
    /// Represents hair line.
    /// </summary>
    Hair = 7,
    /// <summary>
    /// Represents medium dash-dotted line.
    /// </summary>
    MediumDashDot = 10,
    /// <summary>
    /// Represents medium dash-dot-dotted line.
    /// </summary>
    MediumDashDotDot = 12,
    /// <summary>
    /// Represents medium dashed line.
    /// </summary>
    MediumDashed = 8,
    /// <summary>
    /// Represents no line.
    /// </summary>
    None = 0,
    /// <summary>
    /// Represents medium line.
    /// </summary>
    Medium = 2,
    /// <summary>
    /// Represents slanted medium dash-dotted line.
    /// </summary>
    SlantedDashDot = 13,
    /// <summary>
    /// Represents thick line.
    /// </summary>
    Thick = 5,
    /// <summary>
    /// Represents thin line.
    /// </summary>
    Thin = 1,
};

} }

#endif
