#ifndef ASPOSE_CELLS_CHARTS_CHARTTEXTDIRECTIONTYPE_H
#define ASPOSE_CELLS_CHARTS_CHARTTEXTDIRECTIONTYPE_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents the text direction type of the chart.
/// </summary>
enum class ChartTextDirectionType {
    /// <summary>
    /// Horizontal direction type.
    /// </summary>
    Horizontal,
    /// <summary>
    /// Vertical direction type.
    /// </summary>
    Vertical,
    /// <summary>
    /// Rotate 90 angle.
    /// </summary>
    Rotate90,
    /// <summary>
    /// Rotate 270 angle.
    /// </summary>
    Rotate270,
    /// <summary>
    /// Stacked text.
    /// </summary>
    Stacked,
};

} } }

#endif
