#ifndef ASPOSE_CELLS_RENDERING_COLORDEPTH_H
#define ASPOSE_CELLS_RENDERING_COLORDEPTH_H

namespace Aspose { namespace Cells { namespace Rendering {

/// <summary>
/// Enumerates Bit Depth Type for tiff image.
/// </summary>
enum class ColorDepth {
    /// <summary>
    /// Default value, not set value.
    /// </summary>
    Default = 0,
    /// <summary>
    /// 1 bit per pixel
    /// </summary>
    Format1bpp = 1,
    /// <summary>
    /// 4 bits per pixel
    /// </summary>
    Format4bpp = 4,
    /// <summary>
    /// 8 bits per pixel
    /// </summary>
    Format8bpp = 8,
    /// <summary>
    /// 24 bits per pixel
    /// </summary>
    Format24bpp = 24,
    /// <summary>
    /// 32 bits per pixel
    /// </summary>
    Format32bpp = 32,
};

} } }

#endif
