#ifndef ASPOSE_CELLS_RENDERING_COMMENTTITLETYPE_H
#define ASPOSE_CELLS_RENDERING_COMMENTTITLETYPE_H

namespace Aspose { namespace Cells { namespace Rendering {

/// <summary>
/// Represents comment title type while rendering when comment is set to display at end of sheet.
/// </summary>
enum class CommentTitleType {
    /// <summary>
    /// Represents comment title cell.
    /// </summary>
    Cell,
    /// <summary>
    /// Represents comment title comment.
    /// </summary>
    Comment,
    /// <summary>
    /// Represents comment title note.
    /// </summary>
    Note,
    /// <summary>
    /// Represents comment title reply.
    /// </summary>
    Reply,
};

} } }

#endif
