#ifndef ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLLISTSTYLE_H
#define ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLLISTSTYLE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace ActiveXControls {

/// <summary>
/// Represents the visual appearance of the list in a ListBox or ComboBox.
/// </summary>
enum class ControlListStyle {
    /// <summary>
    /// Displays a list in which the background of an item is highlighted when it is selected.
    /// </summary>
    Plain = 0,
    /// <summary>
    /// Displays a list in which an option button or a checkbox next to each entry displays the selection state of that item.
    /// </summary>
    Option = 1,
};

} } } }

#endif
