#ifndef ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLPICTUREALIGNMENTTYPE_H
#define ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLPICTUREALIGNMENTTYPE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace ActiveXControls {

/// <summary>
/// Represents the alignment of the picture inside the Form or Image.
/// </summary>
enum class ControlPictureAlignmentType {
    /// <summary>
    /// The top left corner.
    /// </summary>
    TopLeft = 0,
    /// <summary>
    /// The top right corner.
    /// </summary>
    TopRight = 1,
    /// <summary>
    /// The center.
    /// </summary>
    Center = 2,
    /// <summary>
    /// The bottom left corner.
    /// </summary>
    BottomLeft = 3,
    /// <summary>
    /// The bottom right corner.
    /// </summary>
    BottomRight = 4,
};

} } } }

#endif
