#ifndef ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLSCROLLBARTYPE_H
#define ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLSCROLLBARTYPE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace ActiveXControls {

/// <summary>
/// Represents the type of scroll bar.
/// </summary>
enum class ControlScrollBarType {
    /// <summary>
    /// Displays no scroll bars.
    /// </summary>
    None = 0,
    /// <summary>
    /// Displays a horizontal scroll bar.
    /// </summary>
    Horizontal = 1,
    /// <summary>
    /// Displays a vertical scroll bar.
    /// </summary>
    BarsVertical = 2,
    /// <summary>
    /// Displays both a horizontal and a vertical scroll bar.
    /// </summary>
    BarsBoth = 3,
};

} } } }

#endif
