#ifndef ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLSCROLLORIENTATION_H
#define ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_CONTROLSCROLLORIENTATION_H

namespace Aspose { namespace Cells { namespace Drawing { namespace ActiveXControls {

/// <summary>
/// Represents type of scroll orientation
/// </summary>
enum class ControlScrollOrientation {
    /// <summary>
    /// Control is rendered horizontally when the control's width is greater than its height.
    /// Control is rendered vertically otherwise.
    /// </summary>
    Auto = 3,
    /// <summary>
    /// Control is rendered vertically.
    /// </summary>
    Vertical = 0,
    /// <summary>
    /// Control is rendered horizontally.
    /// </summary>
    Horizontal = 1,
};

} } } }

#endif
