#ifndef ASPOSE_CELLS_COPYFORMATTYPE_H
#define ASPOSE_CELLS_COPYFORMATTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents type of copying format when inserting rows.
/// </summary>
enum class CopyFormatType {
    /// <summary>
    /// Formats same as above row.
    /// </summary>
    SameAsAbove,
    /// <summary>
    /// Formats same as below row.
    /// </summary>
    SameAsBelow,
    /// <summary>
    /// Clears formatting.
    /// </summary>
    Clear,
};

} }

#endif
