#ifndef ASPOSE_CELLS_CHARTS_CROSSTYPE_H
#define ASPOSE_CELLS_CHARTS_CROSSTYPE_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents the axis cross type.
/// </summary>
enum class CrossType {
    /// <summary>
    /// Microsoft Excel sets the axis crossing point.
    /// </summary>
    Automatic,
    /// <summary>
    /// The axis crosses at the maximum value.
    /// </summary>
    Maximum,
    /// <summary>
    /// The axis crosses at the minimum value.
    /// </summary>
    Minimum,
    /// <summary>
    /// The axis crosses at the custom value.
    /// </summary>
    Custom,
};

} } }

#endif
