#ifndef ASPOSE_CELLS_DATABARBORDERTYPE_H
#define ASPOSE_CELLS_DATABARBORDERTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Specifies the border type of a data bar.
/// </summary>
enum class DataBarBorderType {
    /// <summary>
    /// The data bar has no border.
    /// </summary>
    None = 0,
    /// <summary>
    /// The data bar has a solid border.
    /// </summary>
    Solid = 1,
};

} }

#endif
