#ifndef ASPOSE_CELLS_CHARTS_DATALABELSSEPARATORTYPE_H
#define ASPOSE_CELLS_CHARTS_DATALABELSSEPARATORTYPE_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents the separator type of DataLabels.
/// </summary>
enum class DataLabelsSeparatorType {
    /// <summary>
    /// Represents automatic separator
    /// </summary>
    Auto = 0,
    /// <summary>
    /// Represents space(" ")
    /// </summary>
    Space = 1,
    /// <summary>
    /// Represents comma(",")
    /// </summary>
    Comma = 2,
    /// <summary>
    /// Represents semicolon(";")
    /// </summary>
    Semicolon = 3,
    /// <summary>
    /// Represents period(".")
    /// </summary>
    Period = 4,
    /// <summary>
    /// Represents newline("\n")
    /// </summary>
    NewLine = 5,
    /// <summary>
    /// Represents custom separator
    /// </summary>
    Custom = 6,
};

} } }

#endif
