#ifndef ASPOSE_CELLS_CHARTS_DATALABLESSEPARATORTYPE_H
#define ASPOSE_CELLS_CHARTS_DATALABLESSEPARATORTYPE_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents the separator type of DataLabels.
/// </summary>
/// <remarks>
/// NOTE: This member is now obsolete. Instead,
/// please use DataLabelsSeparatorType enum.
/// This property will be removed 12 months later since September 2020.
/// Aspose apologizes for any inconvenience you may have experienced.</remarks>
/// @deprecated Use DataLabelsSeparatorType enum instead.
enum class DataLablesSeparatorType {
    /// <summary>
    /// Represents automatic separator
    /// </summary>
    Auto = 0,
    /// <summary>
    /// Represents space(" ")
    /// </summary>
    Space = 1,
    /// <summary>
    /// Represents comma(",")
    /// </summary>
    Comma = 2,
    /// <summary>
    /// Represents semicolon(";")
    /// </summary>
    Semicolon = 3,
    /// <summary>
    /// Represents period(".")
    /// </summary>
    Period = 4,
    /// <summary>
    /// Represents newline("\n")
    /// </summary>
    NewLine = 5,
    /// <summary>
    /// Represents custom separator
    /// </summary>
    Custom = 6,
};

} } }

#endif
