#ifndef ASPOSE_CELLS_DATE_H
#define ASPOSE_CELLS_DATE_H

#include <stdint.h>

#include "Aspose.Cells/Export.h"

namespace Aspose { namespace Cells {
	/// <summary>
	/// Represents a date and time.
	/// </summary>
	struct Date {
		int32_t	year;
		int32_t	month;
		int32_t	day;
		int32_t	hour;
		int32_t	minute;
		int32_t	second;
		int32_t millisecond;
	};
} }

#endif
