#ifndef ASPOSE_CELLS_DATETIMEGROUPINGTYPE_H
#define ASPOSE_CELLS_DATETIMEGROUPINGTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Specifies how to group dateTime values.
/// </summary>
enum class DateTimeGroupingType {
    /// <summary>
    /// Group by day.
    /// </summary>
    Day,
    /// <summary>
    /// Group by hour.
    /// </summary>
    Hour,
    /// <summary>
    /// Group by Minute.
    /// </summary>
    Minute,
    /// <summary>
    /// Group by Month.
    /// </summary>
    Month,
    /// <summary>
    /// Group by Second.
    /// </summary>
    Second,
    /// <summary>
    /// Group by Year.
    /// </summary>
    Year,
};

} }

#endif
