#ifndef ASPOSE_CELLS_DEFAULTEDITLANGUAGE_H
#define ASPOSE_CELLS_DEFAULTEDITLANGUAGE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents the default edit language.
/// </summary>
enum class DefaultEditLanguage {
    /// <summary>
    /// Represents auto detecting edit language according to the text itself.
    /// </summary>
    Auto,
    /// <summary>
    /// Represents English language.
    /// </summary>
    English,
    /// <summary>
    /// Represents Chinese, Japanese, Korean language.
    /// </summary>
    CJK,
};

} }

#endif
