#ifndef ASPOSE_CELLS_DISPLAYDRAWINGOBJECTS_H
#define ASPOSE_CELLS_DISPLAYDRAWINGOBJECTS_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents whether and how to show objects in the workbook.
/// </summary>
enum class DisplayDrawingObjects {
    /// <summary>
    /// Show all objects
    /// </summary>
    DisplayShapes,
    /// <summary>
    /// Show placeholders
    /// </summary>
    Placeholders,
    /// <summary>
    /// Hide all shapes.
    /// </summary>
    Hide,
};

} }

#endif
