#ifndef ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_DROPBUTTONSTYLE_H
#define ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_DROPBUTTONSTYLE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace ActiveXControls {

/// <summary>
/// Represents the symbol displayed on the drop button.
/// </summary>
enum class DropButtonStyle {
    /// <summary>
    /// Displays a button with no symbol.
    /// </summary>
    Plain,
    /// <summary>
    /// Displays a button with a down arrow.
    /// </summary>
    Arrow,
    /// <summary>
    /// Displays a button with an ellipsis (...).
    /// </summary>
    Ellipsis,
    /// <summary>
    /// Displays a button with a horizontal line like an underscore character.
    /// </summary>
    Reduce,
};

} } } }

#endif
