#ifndef ASPOSE_CELLS_ENCODING_TYPE_H
#define ASPOSE_CELLS_ENCODING_TYPE_H

namespace Aspose { namespace Cells {
	/// <summary>
	/// Enums supported encoding types.
	/// </summary>
	enum class EncodingType {
		/// <summary>
		/// Default encoding type.
		/// </summary>
		Default,
		/// <summary>
		/// ASCII encoding type.
		/// </summary>
		ASCII,
		/// <summary>
		/// UTF8 encoding type.
		/// </summary>
		UTF8,
		/// <summary>
		/// Little-endian Unicode encoding type.
		/// </summary>
		Unicode,
		/// <summary>
		/// Big-endian Unicode encoding type.
		/// </summary>
		UnicodeBE
	};
} }

#endif
