#ifndef ASPOSE_CELLS_DRAWING_EQUATIONS_EQUATIONDELIMITERSHAPETYPE_H
#define ASPOSE_CELLS_DRAWING_EQUATIONS_EQUATIONDELIMITERSHAPETYPE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace Equations {

/// <summary>
/// This specifies the shape of delimiters in the delimiter object.
/// </summary>
enum class EquationDelimiterShapeType {
    /// <summary>
    /// The divider is centered around the entire height of its content.
    /// </summary>
    Centered,
    /// <summary>
    /// The divider is altered to exactly match their contents' height.
    /// </summary>
    Match,
};

} } } }

#endif
