#ifndef ASPOSE_CELLS_DRAWING_EQUATIONS_EQUATIONLIMITLOCATIONTYPE_H
#define ASPOSE_CELLS_DRAWING_EQUATIONS_EQUATIONLIMITLOCATIONTYPE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace Equations {

/// <summary>
/// Specifies the limit location on an operator.
/// </summary>
enum class EquationLimitLocationType {
    /// <summary>
    /// Specifies that the limit is centered above or below the operator.
    /// </summary>
    UndOvr,
    /// <summary>
    /// Specifies that the limit is on the right side of the operator.
    /// </summary>
    SubSup,
};

} } } }

#endif
