#ifndef ASPOSE_CELLS_FONTSCHEMETYPE_H
#define ASPOSE_CELLS_FONTSCHEMETYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents the scheme type of the font.
/// </summary>
enum class FontSchemeType {
    /// <summary>
    /// None
    /// </summary>
    None,
    /// <summary>
    /// Major scheme.
    /// </summary>
    Major,
    /// <summary>
    /// Minor scheme.
    /// </summary>
    /// <remarks>
    /// The font's name will be automatically changed with the language.
    /// </remarks>
    Minor,
};

} }

#endif
