#ifndef ASPOSE_CELLS_FONTSOURCETYPE_H
#define ASPOSE_CELLS_FONTSOURCETYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Specifies the type of a font source.
/// </summary>
enum class FontSourceType {
    /// <summary>
    /// represents single font file.
    /// </summary>
    FontFile = 0,
    /// <summary>
    /// represents folder with font files.
    /// </summary>
    FontsFolder = 1,
    /// <summary>
    /// represents single font in memory.
    /// </summary>
    MemoryFont = 2,
};

} }

#endif
