#ifndef ASPOSE_CELLS_CHARTS_FORMATTINGTYPE_H
#define ASPOSE_CELLS_CHARTS_FORMATTINGTYPE_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents the type of formatting applied to an <see cref="Area"/> object or a <see cref="Line"/> object.
/// </summary>
enum class FormattingType {
    /// <summary>
    /// Represents automatic formatting type.
    /// </summary>
    Automatic = 0,
    /// <summary>
    /// Represents custom formatting type.
    /// </summary>
    Custom = 2,
    /// <summary>
    /// Represents none formatting type.
    /// </summary>
    None = 1,
};

} } }

#endif
