#ifndef ASPOSE_CELLS_DRAWING_GRADIENTCOLORTYPE_H
#define ASPOSE_CELLS_DRAWING_GRADIENTCOLORTYPE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Represents the gradient color type for the specified fill.
/// </summary>
enum class GradientColorType {
    /// <summary>
    /// No gradient color
    /// </summary>
    None,
    /// <summary>
    /// One gradient color
    /// </summary>
    OneColor,
    /// <summary>
    /// Preset gradient colors
    /// </summary>
    PresetColors,
    /// <summary>
    /// Two gradient colors
    /// </summary>
    TwoColors,
};

} } }

#endif
