#ifndef ASPOSE_CELLS_ICONSETTYPE_H
#define ASPOSE_CELLS_ICONSETTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Icon set type for conditional formatting.
/// The threshold values for triggering the different icons within a set are
/// configurable, and the icon order is reversible.
/// </summary>
enum class IconSetType {
    /// <summary>
    /// 3 arrows icon set.
    /// </summary>
    Arrows3,
    /// <summary>
    /// 3 gray arrows icon set.
    /// </summary>
    ArrowsGray3,
    /// <summary>
    /// 3 flags icon set.
    /// </summary>
    Flags3,
    /// <summary>
    /// 3 signs icon set.
    /// </summary>
    Signs3,
    /// <summary>
    /// 3 symbols icon set (circled).
    /// </summary>
    Symbols3,
    /// <summary>
    /// 3 Symbols icon set (uncircled).
    /// </summary>
    Symbols32,
    /// <summary>
    /// 3 traffic lights icon set (unrimmed).
    /// </summary>
    TrafficLights31,
    /// <summary>
    /// 3 traffic lights icon set with thick black border.
    /// </summary>
    TrafficLights32,
    /// <summary>
    /// 4 arrows icon set.
    /// </summary>
    Arrows4,
    /// <summary>
    /// 4 gray arrows icon set.
    /// </summary>
    ArrowsGray4,
    /// <summary>
    /// 4 ratings icon set.
    /// </summary>
    Rating4,
    /// <summary>
    /// 4 'red to black' icon set.
    /// </summary>
    RedToBlack4,
    /// <summary>
    /// 4 traffic lights icon set.
    /// </summary>
    TrafficLights4,
    /// <summary>
    /// 5 arrows icon set.
    /// </summary>
    Arrows5,
    /// <summary>
    /// 5 gray arrows icon set.
    /// </summary>
    ArrowsGray5,
    /// <summary>
    /// 5 quarters icon set.
    /// </summary>
    Quarters5,
    /// <summary>
    /// 5 rating icon set.
    /// </summary>
    Rating5,
    /// <summary>
    /// 3 stars set
    /// </summary>
    Stars3,
    /// <summary>
    /// 5 boxes set
    /// </summary>
    Boxes5,
    /// <summary>
    /// 3 triangles set
    /// </summary>
    Triangles3,
    /// <summary>
    /// None
    /// </summary>
    None,
    /// <summary>
    /// CustomSet.
    /// This element is read-only.
    /// </summary>
    CustomSet,
    /// <summary>
    /// 3 smilies.
    /// Only for .ods.
    /// </summary>
    Smilies3,
    /// <summary>
    /// 3 color smilies.
    /// Only for .ods.
    /// </summary>
    ColorSmilies3,
};

} }

#endif
