#ifndef ASPOSE_CELLS_RENDERING_IMAGEBINARIZATIONMETHOD_H
#define ASPOSE_CELLS_RENDERING_IMAGEBINARIZATIONMETHOD_H

namespace Aspose { namespace Cells { namespace Rendering {

/// <summary>
/// Specifies the method used to binarize image.
/// </summary>
enum class ImageBinarizationMethod {
    /// <summary>
    /// Specifies threshold method.
    /// </summary>
    Threshold = 0,
    /// <summary>
    /// Specifies dithering using Floyd-Steinberg error diffusion method.
    /// </summary>
    FloydSteinbergDithering = 1,
};

} } }

#endif
