#ifndef ASPOSE_CELLS_DRAWING_LINECAPTYPE_H
#define ASPOSE_CELLS_DRAWING_LINECAPTYPE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Represents the caps of a line
/// </summary>
enum class LineCapType {
    /// <summary>
    /// Square protrudes by half line width.
    /// </summary>
    Square,
    /// <summary>
    /// Rounded ends.
    /// </summary>
    Round,
    /// <summary>
    /// Line ends at end point.
    /// </summary>
    Flat,
    /// <summary>
    /// None cap
    /// </summary>
    None,
};

} } }

#endif
