#ifndef ASPOSE_CELLS_DRAWING_LINETYPE_H
#define ASPOSE_CELLS_DRAWING_LINETYPE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Enumerates the type of <see cref="Picture"/> border or <see cref="Chart"/> line.
/// </summary>
enum class LineType {
    /// <summary>
    /// Represents a dark gray line.
    /// </summary>
    DarkGray = 6,
    /// <summary>
    /// Represent a dash line.
    /// </summary>
    Dash = 1,
    /// <summary>
    /// Represents a dash-dot line
    /// </summary>
    DashDot = 3,
    /// <summary>
    /// Represents a dash-dot-dot line.
    /// </summary>
    DashDotDot = 4,
    /// <summary>
    /// Represents a dotted line.
    /// </summary>
    Dot = 2,
    /// <summary>
    /// Represents a light gray line.
    /// </summary>
    LightGray = 8,
    /// <summary>
    /// Represents a medium gray line.
    /// </summary>
    MediumGray = 7,
    /// <summary>
    /// Represent a solid line.
    /// </summary>
    Solid = 0,
};

} } }

#endif
