#ifndef ASPOSE_CELLS_DRAWING_MSOARROWHEADSTYLE_H
#define ASPOSE_CELLS_DRAWING_MSOARROWHEADSTYLE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Enumerates the line end type of the shape border line.
/// </summary>
enum class MsoArrowheadStyle {
    /// <summary>
    /// No line end type.
    /// </summary>
    None = 0,
    /// <summary>
    /// Arrow line end type.
    /// </summary>
    Arrow = 1,
    /// <summary>
    /// Arrow Stealth line end type.
    /// </summary>
    ArrowStealth = 2,
    /// <summary>
    /// Arrow Diamond Line end type.
    /// </summary>
    ArrowDiamond = 3,
    /// <summary>
    /// Arrow Oval line end type.
    /// </summary>
    ArrowOval = 4,
    /// <summary>
    /// Arrow Open line end type.
    /// </summary>
    ArrowOpen = 5,
};

} } }

#endif
