#ifndef ASPOSE_CELLS_DRAWING_MSOLINEDASHSTYLE_H
#define ASPOSE_CELLS_DRAWING_MSOLINEDASHSTYLE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Represents style of dash drawing lines.
/// </summary>
enum class MsoLineDashStyle {
    /// <summary>
    /// Represent a dash line.
    /// </summary>
    Dash,
    /// <summary>
    /// Represents a dash-dot line.
    /// </summary>
    DashDot,
    /// <summary>
    /// Represents a dash-dot-dot line.
    /// </summary>
    DashDotDot,
    /// <summary>
    /// Represents a long dash-short dash line.
    /// </summary>
    DashLongDash,
    /// <summary>
    /// Represents a long dash-short dash-dot line.
    /// </summary>
    DashLongDashDot,
    /// <summary>
    /// Represents a round-dot line.
    /// </summary>
    RoundDot,
    /// <summary>
    /// Represent a solid line.
    /// </summary>
    Solid,
    /// <summary>
    /// Represents a square-dot line.
    /// </summary>
    SquareDot,
    /// <summary>
    /// Custom dash style.
    /// </summary>
    Custom,
};

} } }

#endif
