#ifndef ASPOSE_CELLS_NAMESCOPETYPE_H
#define ASPOSE_CELLS_NAMESCOPETYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents the scope type of defined names.
/// </summary>
enum class NameScopeType {
    /// <summary>
    /// All defined names.
    /// </summary>
    All,
    /// <summary>
    /// The defined names in the workbook.
    /// </summary>
    Workbook,
    /// <summary>
    /// The defined names in a worksheet or all worksheets.
    /// </summary>
    Worksheet,
};

} }

#endif
