#ifndef ASPOSE_CELLS_OOXMLCOMPLIANCE_H
#define ASPOSE_CELLS_OOXMLCOMPLIANCE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Allows to specify which OOXML specification will be used when saving in the Xlsx format.
/// </summary>
enum class OoxmlCompliance {
    /// <summary>
    /// ECMA-376 1st Edition, 2006.
    /// </summary>
    Ecma376_2006,
    /// <summary>
    /// ISO/IEC 29500:2008 Strict compliance level.
    /// </summary>
    Iso29500_2008_Strict,
};

} }

#endif
