#ifndef ASPOSE_CELLS_RENDERING_PDFCOMPRESSIONCORE_H
#define ASPOSE_CELLS_RENDERING_PDFCOMPRESSIONCORE_H

namespace Aspose { namespace Cells { namespace Rendering {

/// <summary>
/// Specifies a type of compression applied to all content in the PDF file except images.
/// </summary>
enum class PdfCompressionCore {
    /// <summary>
    /// None
    /// </summary>
    None,
    /// <summary>
    /// Rle
    /// </summary>
    Rle,
    /// <summary>
    /// Lzw
    /// </summary>
    Lzw,
    /// <summary>
    /// Flate
    /// </summary>
    Flate,
};

} } }

#endif
