#ifndef ASPOSE_CELLS_PIVOT_PIVOTFIELDGROUPTYPE_H
#define ASPOSE_CELLS_PIVOT_PIVOTFIELDGROUPTYPE_H

namespace Aspose { namespace Cells { namespace Pivot {

/// <summary>
/// Represents the group type of pivot field.
/// </summary>
enum class PivotFieldGroupType {
    /// <summary>
    /// No group
    /// </summary>
    None,
    /// <summary>
    /// Grouped by DateTime range.
    /// </summary>
    DateTimeRange,
    /// <summary>
    /// Grouped by numberic range.
    /// </summary>
    NumbericRange,
    /// <summary>
    /// Grouped by discrete points.
    /// </summary>
    Discrete,
};

} } }

#endif
