#ifndef ASPOSE_CELLS_PIVOT_PIVOTFIELDTYPE_H
#define ASPOSE_CELLS_PIVOT_PIVOTFIELDTYPE_H

namespace Aspose { namespace Cells { namespace Pivot {

/// <summary>
/// Represents PivotTable field type.
/// </summary>
enum class PivotFieldType {
    /// <summary>
    /// Presents base pivot field type.
    /// </summary>
    Undefined = 0,
    /// <summary>
    /// Presents row pivot field type.
    /// </summary>
    Row = 1,
    /// <summary>
    /// Presents column pivot field type.
    /// </summary>
    Column = 2,
    /// <summary>
    /// Presents page pivot field type.
    /// </summary>
    Page = 4,
    /// <summary>
    /// Presents data pivot field type.
    /// </summary>
    Data = 8,
};

} } }

#endif
