#ifndef ASPOSE_CELLS_PIVOT_PIVOTTABLESELECTIONTYPE_H
#define ASPOSE_CELLS_PIVOT_PIVOTTABLESELECTIONTYPE_H

namespace Aspose { namespace Cells { namespace Pivot {

/// <summary>
/// Specifies what can be selected in a PivotTable during a structured selection.
/// These constants can be combined to select multiple types.
/// </summary>
enum class PivotTableSelectionType {
    /// <summary>
    /// Data and labels
    /// </summary>
    DataAndLabel = 0,
    /// <summary>
    /// Only selects data
    /// </summary>
    DataOnly = 2,
    /// <summary>
    /// Only selects labels
    /// </summary>
    LabelOnly = 1,
};

} } }

#endif
