#ifndef ASPOSE_CELLS_PRINTCOMMENTSTYPE_H
#define ASPOSE_CELLS_PRINTCOMMENTSTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents the way comments are printed with the sheet.
/// </summary>
enum class PrintCommentsType {
    /// <summary>
    /// Represents to print comments as displayed on sheet.
    /// </summary>
    PrintInPlace,
    /// <summary>
    /// Represents not to print comments.
    /// </summary>
    PrintNoComments,
    /// <summary>
    /// Represents to print comments at end of sheet.
    /// </summary>
    PrintSheetEnd,
};

} }

#endif
