#ifndef ASPOSE_CELLS_PRINTERRORSTYPE_H
#define ASPOSE_CELLS_PRINTERRORSTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents print errors constants.
/// </summary>
enum class PrintErrorsType {
    /// <summary>
    /// Represents not to print errors.
    /// </summary>
    PrintErrorsBlank,
    /// <summary>
    /// Represents to print errors as "--".
    /// </summary>
    PrintErrorsDash,
    /// <summary>
    /// Represents to print errors as displayed.
    /// </summary>
    PrintErrorsDisplayed,
    /// <summary>
    /// Represents to print errors as "#N/A".
    /// </summary>
    PrintErrorsNA,
};

} }

#endif
