#ifndef ASPOSE_CELLS_DRAWING_REFLECTIONEFFECTTYPE_H
#define ASPOSE_CELLS_DRAWING_REFLECTIONEFFECTTYPE_H

namespace Aspose { namespace Cells { namespace Drawing {

/// <summary>
/// Represents the effect type of reflection.
/// </summary>
enum class ReflectionEffectType {
    /// <summary>
    /// No reflection effect.
    /// </summary>
    None,
    /// <summary>
    /// Custom reflection effect.
    /// </summary>
    Custom,
    /// <summary>
    /// Tight reflection, touching.
    /// </summary>
    TightReflectionTouching,
    /// <summary>
    /// Half reflection, touching.
    /// </summary>
    HalfReflectionTouching,
    /// <summary>
    /// Full reflection, touching.
    /// </summary>
    FullReflectionTouching,
    /// <summary>
    /// Tight reflection, 4 pt offset.
    /// </summary>
    TightReflection4PtOffset,
    /// <summary>
    /// Half reflection, 4 pt offset.
    /// </summary>
    HalfReflection4PtOffset,
    /// <summary>
    /// Full reflection, 4 pt offset.
    /// </summary>
    FullReflection4PtOffset,
    /// <summary>
    /// Tight reflection, 8 pt offset.
    /// </summary>
    TightReflection8PtOffset,
    /// <summary>
    /// Half reflection, 8 pt offset.
    /// </summary>
    HalfReflection8PtOffset,
    /// <summary>
    /// Full reflection, 8 pt offset.
    /// </summary>
    FullReflection8PtOffset,
};

} } }

#endif
