#ifndef ASPOSE_CELLS_REVISIONS_REVISIONTYPE_H
#define ASPOSE_CELLS_REVISIONS_REVISIONTYPE_H

namespace Aspose { namespace Cells { namespace Revisions {

/// <summary>
/// Represents the revision type.
/// </summary>
enum class RevisionType {
    /// <summary>
    /// Custom view.
    /// </summary>
    CustomView,
    /// <summary>
    /// Defined name.
    /// </summary>
    DefinedName,
    /// <summary>
    /// Cells change.
    /// </summary>
    ChangeCells,
    /// <summary>
    /// Auto format.
    /// </summary>
    AutoFormat,
    /// <summary>
    /// Merge conflict.
    /// </summary>
    MergeConflict,
    /// <summary>
    /// Comment.
    /// </summary>
    Comment,
    /// <summary>
    /// Format.
    /// </summary>
    Format,
    /// <summary>
    /// Insert worksheet.
    /// </summary>
    InsertSheet,
    /// <summary>
    /// Move cells.
    /// </summary>
    MoveCells,
    /// <summary>
    /// Undo.
    /// </summary>
    Undo,
    /// <summary>
    /// Query table.
    /// </summary>
    QueryTable,
    /// <summary>
    /// Inserting or deleting.
    /// </summary>
    InsertDelete,
    /// <summary>
    /// Rename worksheet.
    /// </summary>
    RenameSheet,
    /// <summary>
    /// Unknown.
    /// </summary>
    Unknown,
};

} } }

#endif
