#ifndef ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_SHOWDROPBUTTONTYPE_H
#define ASPOSE_CELLS_DRAWING_ACTIVEXCONTROLS_SHOWDROPBUTTONTYPE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace ActiveXControls {

/// <summary>
/// Specifies when to show the drop button
/// </summary>
enum class ShowDropButtonType {
    /// <summary>
    /// Never show the drop button.
    /// </summary>
    Never,
    /// <summary>
    /// Show the drop button when the control has the focus.
    /// </summary>
    Focus,
    /// <summary>
    /// Always show the drop button.
    /// </summary>
    Always,
};

} } } }

#endif
