#ifndef ASPOSE_CELLS_CHARTS_SPARKLINEAXISMINMAXTYPE_H
#define ASPOSE_CELLS_CHARTS_SPARKLINEAXISMINMAXTYPE_H

namespace Aspose { namespace Cells { namespace Charts {

/// <summary>
/// Represents the minimum and maximum value types for the sparkline vertical axis.
/// </summary>
enum class SparklineAxisMinMaxType {
    /// <summary>
    /// Automatic for each sparkline.
    /// </summary>
    AutoIndividual,
    /// <summary>
    /// Same for all sparklines in the group.
    /// </summary>
    Group,
    /// <summary>
    /// Custom value for sparkline.
    /// </summary>
    Custom,
};

} } }

#endif
