#ifndef ASPOSE_CELLS_DRAWING_TEXTS_TEXTTABALIGNMENTTYPE_H
#define ASPOSE_CELLS_DRAWING_TEXTS_TEXTTABALIGNMENTTYPE_H

namespace Aspose { namespace Cells { namespace Drawing { namespace Texts {

/// <summary>
/// Represents the text tab alignment types.
/// </summary>
enum class TextTabAlignmentType {
    /// <summary>
    /// The text at this tab stop is center aligned.
    /// </summary>
    Center,
    /// <summary>
    /// At this tab stop, the decimals are lined up.
    /// </summary>
    Decimal,
    /// <summary>
    /// The text at this tab stop is left aligned.
    /// </summary>
    Left,
    /// <summary>
    /// The text at this tab stop is right aligned.
    /// </summary>
    Right,
};

} } } }

#endif
