#ifndef ASPOSE_CELLS_VALIDATIONTYPE_H
#define ASPOSE_CELLS_VALIDATIONTYPE_H

namespace Aspose { namespace Cells {

/// <summary>
/// Represents data validation type.
/// </summary>
enum class ValidationType {
    /// <summary>
    /// Any value validation type.
    /// </summary>
    AnyValue,
    /// <summary>
    /// Whole number validation type.
    /// </summary>
    WholeNumber,
    /// <summary>
    /// Decimal validation type.
    /// </summary>
    Decimal,
    /// <summary>
    /// List validation type.
    /// </summary>
    List,
    /// <summary>
    /// Date validation type.
    /// </summary>
    Date,
    /// <summary>
    /// Time validation type.
    /// </summary>
    Time,
    /// <summary>
    /// Text length validation type.
    /// </summary>
    TextLength,
    /// <summary>
    /// Custom validation type.
    /// </summary>
    Custom,
};

} }

#endif
