#ifndef ASPOSE_CELLS_VBA_VBAMODULETYPE_H
#define ASPOSE_CELLS_VBA_VBAMODULETYPE_H

namespace Aspose { namespace Cells { namespace Vba {

/// <summary>
/// Represents the type of VBA module.
/// </summary>
enum class VbaModuleType {
    /// <summary>
    /// Represents a procedural module.
    /// </summary>
    Procedural,
    /// <summary>
    /// Represents a document module.
    /// </summary>
    Document,
    /// <summary>
    /// Represents a class module.
    /// </summary>
    Class,
    /// <summary>
    /// Represents a designer module.
    /// </summary>
    Designer,
};

} } }

#endif
