#ifndef ASPOSE_CELLS_VBA_VBAPROJECTREFERENCETYPE_H
#define ASPOSE_CELLS_VBA_VBAPROJECTREFERENCETYPE_H

namespace Aspose { namespace Cells { namespace Vba {

/// <summary>
/// Represents the type of VBA project reference.
/// </summary>
enum class VbaProjectReferenceType {
    /// <summary>
    /// Specifies a reference to an Automation type library.
    /// </summary>
    Registered,
    /// <summary>
    /// Specifies a reference to a twiddled type library and its extended type library.
    /// </summary>
    Control,
    /// <summary>
    /// Specifies a reference to an external VBA project.
    /// </summary>
    Project,
};

} } }

#endif
