#ifndef ASPOSE_CELLS_DIGITALSIGNATURES_XADESTYPE_H
#define ASPOSE_CELLS_DIGITALSIGNATURES_XADESTYPE_H

namespace Aspose { namespace Cells { namespace DigitalSignatures {

/// <summary>
/// Type of XML Advanced Electronic Signature (XAdES).
/// </summary>
enum class XAdESType {
    /// <summary>
    /// XAdES is off.
    /// </summary>
    None = 0x00,
    /// <summary>
    /// Basic XAdES.
    /// </summary>
    XAdES = 0x01,
};

} } }

#endif
